/**
 *  Get pizzas sub total (total pizza * pizza price)
 * @param pizzaList list of pizza
 * @returns subtotal
 */
export const getPizzaSubtotal = pizzaList => {
  return Number(pizzaList.reduce((total, pizza) => {
    total += (pizza.quantity || 0) * pizza.price;
    return total;
  }, 0).toFixed(2));
};
